window.onload=function(){var modal=new RModal(document.getElementById('modal'),{beforeOpen:function(next){console.log('beforeOpen');next();},afterOpen:function(){console.log('opened');},beforeClose:function(next){console.log('beforeClose');next();},afterClose:function(){console.log('closed');}});document.addEventListener('keydown',function(ev){modal.keydown(ev);},false);var elementsArray=document.querySelectorAll(".showModal");elementsArray.forEach(function(elem){elem.addEventListener("click",function(ev){ev.preventDefault();modal.open();},false);});window.modal=modal;};